function initTabs(){let e=document.querySelectorAll(".content-tabs");for(let t=0;t<e.length;t++){let n=e[t],l=n.querySelectorAll(".content-tabs__list-tab"),o=n.querySelectorAll(".content-tabs__contents-block");for(let e=0;e<l.length;e++){let t=l[e];t.addEventListener("click",(function(n){for(let e=0;e<l.length;e++){l[e].classList.remove("open"),o[e].classList.remove("open")}t.classList.add("open"),o[e].classList.add("open"),n.preventDefault()}))}}}initTabs();const ranges=document.querySelectorAll(".input-range-block");function initRange(e){const t=e.querySelector(".behavior-item__value-range"),n=e.querySelector(".score");n.innerHTML=t.value+"px",t.oninput=()=>{n.innerHTML=t.value+"px"}}ranges.forEach((e=>{initRange(e)}));